#!/bin/sh

if [ ! "$#" -eq 1 ]
then
cat <<END

usage: $ ./rfc.sh DEST_DIR

END
  exit
fi

build=$1

mkdir -p $build

for type in j,jazz c,c
do
  IFS=','
  set -- $type

  ./bin/params $1 24  > $build/libjbn_rfc3526_modp1536.$2

  ./bin/params $1 32  > $build/libjbn_rfc3526_modp2048.$2

  # TODO: add the following: 3072, 4096, 6144, 8192
done
